import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminPanel = () => {
  const [adminData, setAdminData] = useState(null);
  const [loginLogs, setLoginLogs] = useState([]);
  const [securityAlert, setSecurityAlert] = useState(false);

  useEffect(() => {
    const fetchAdminData = async () => {
      try {
        const storedAdmin = localStorage.getItem("admin");
        if (storedAdmin) {
          const parsedAdmin = JSON.parse(storedAdmin);
          setAdminData(parsedAdmin);

          // Fetch logs
          const response = await axios.get(
            `http://localhost:8081/api/loginLogs/${parsedAdmin._id}`
          );
          setLoginLogs(response.data);

          // Check for brute-force log, only if alert hasn't been dismissed
          const dismissed = localStorage.getItem("dismissedSecurityAlert");
          if (!dismissed) {
            const bruteForceLog = response.data.find(
              (log) => log.type === "Brute Force Detected"
            );
            if (bruteForceLog) {
              setSecurityAlert(true);
            }
          }
        }
      } catch (error) {
        console.error("Error fetching admin data:", error);
      }
    };

    fetchAdminData();
  }, []);

  const dismissAlert = () => {
    setSecurityAlert(false);
    localStorage.setItem("dismissedSecurityAlert", "true");
  };

  const resetAlert = () => {
    localStorage.removeItem("dismissedSecurityAlert");
    window.location.reload(); // For testing purposes
  };

  return (
    <div>
      <h2>Welcome, {adminData ? adminData.username : "Admin"}</h2>

      {securityAlert && (
        <div
          style={{
            backgroundColor: "#f44336",
            color: "white",
            padding: "10px",
            margin: "10px 0",
            borderRadius: "4px",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
          }}
        >
          <span>‚ö†Ô∏è Security Alert: Brute Force Attempt Detected!</span>
          <button
            onClick={dismissAlert}
            style={{
              marginLeft: "20px",
              backgroundColor: "#fff",
              color: "#f44336",
              border: "none",
              padding: "5px 10px",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Dismiss
          </button>
        </div>
      )}

      <div>
        <h3>Login Logs</h3>
        {loginLogs.length > 0 ? (
          <ul>
            {loginLogs.map((log, index) => (
              <li key={index}>
                {log.timestamp} - {log.type} - IP: {log.ip}
              </li>
            ))}
          </ul>
        ) : (
          <p>No login logs found.</p>
        )}
      </div>

      {/* Reset for testing */}
      <button
        onClick={resetAlert}
        style={{
          marginTop: "20px",
          backgroundColor: "#1976d2",
          color: "#fff",
          padding: "8px 12px",
          border: "none",
          borderRadius: "4px",
          cursor: "pointer",
        }}
      >
        üîÑ Reset Alert (for testing)
      </button>
    </div>
  );
};

export default AdminPanel;
