import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './ProductDetail.css';

function ProductDetail() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      const response = await fetch(`http://localhost/backend/api/get_product.php?id=${id}`);
      const data = await response.json();
      
      if (data.success) {
        setProduct(data.product);
      } else {
        setError(data.error);
      }
    } catch (error) {
      setError('Failed to fetch product');
    } finally {
      setLoading(false);
    }
  };

  const handleQuantityChange = (value) => {
    const newQuantity = Math.max(1, Math.min(100, quantity + value));
    setQuantity(newQuantity);
  };

  const calculateTotal = () => {
    if (!product) return 0;
    return (product.price * quantity).toFixed(2);
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!product) return <div className="error">Product not found</div>;

  return (
    <div className="product-detail-container">
      <div className="product-detail-grid">
        <div className="product-image-section">
          <img 
            src={`http://localhost/backend/${product.image_url}`} 
            alt={product.name} 
            className="main-image"
          />
        </div>

        <div className="product-info-section">
          <h1>{product.name}</h1>
          <div className="price-section">
            <span className="current-price">NPR {calculateTotal()}</span>
            <span className="original-price">
              NPR {(product.price * quantity * 1.25).toFixed(2)}
            </span>
            <span className="discount">5% off</span>
          </div>

          <div className="description">
            <p>{product.description}</p>
          </div>

          <div className="specifications">
            <div className="spec-item">
              <span className="spec-label">Dimensions:</span>
              <span className="spec-value">{product.dimensions}</span>
            </div>
            <div className="spec-item">
              <span className="spec-label">Style:</span>
              <span className="spec-value">{product.style}</span>
            </div>
          </div>

          <div className="quantity-section">
            <span className="quantity-label">Quantity:</span>
            <div className="quantity-controls">
              <button 
                onClick={() => handleQuantityChange(-1)}
                className="quantity-btn"
                disabled={quantity <= 1}
              >
                -
              </button>
              <span className="quantity-display">{quantity}</span>
              <button 
                onClick={() => handleQuantityChange(1)}
                className="quantity-btn"
                disabled={quantity >= 100}
              >
                +
              </button>
            </div>
          </div>

          <div className="actions">
            <button className="add-to-cart-btn">
              Add to Cart - NPR {calculateTotal()}
            </button>
          </div>

          <div className="safety-info">
            <h3>SAFETY</h3>
            <p>
              Our stickers are made with high-quality materials and are safe for use
              on various surfaces. They are water-resistant and durable.
            </p>
          </div>

          <div className="specification-info">
            <h3>SPECIFICATION</h3>
            <p>
              Premium quality stickers with excellent print quality and durability.
              Perfect for business packaging, laptops, water bottles, and more.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
