import { Link } from 'react-router-dom';
import BannerCarousel from '../components/BannerCarousel';
import './Home.css';

function Home() {
  return (
    <div className="home">
      <BannerCarousel />
      <div className="hero">
        <div className="hero-content">
          <h1>Thank You Stickers Nepal</h1>
          <p>Premium quality stickers for your packaging needs</p>
          <Link to="/products" className="cta-button">
            Explore Collection
          </Link>
        </div>
      </div>
      
      <section className="featured-products">
        <h2>Featured Products</h2>
        <div className="product-grid">
          {[1, 2, 3].map((item) => (
            <div key={item} className="product-card">
              <img 
                src={`/images/product-${item}.jpg`} 
                alt={`Featured product ${item}`} 
              />
              <div className="product-info">
                <h3>Premium Sticker {item}</h3>
                <p>High-quality thank you stickers</p>
                <Link to="/products" className="view-more">
                  View Details
                </Link>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default Home;
