import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Products.css';

function Products() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState('all');

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost/backend/api/get_products.php');
      const data = await response.json();
      
      if (data.success) {
        setProducts(data.products);
      } else {
        setError(data.error);
      }
    } catch (error) {
      setError('Failed to fetch products');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return (
    <div className="loading-container">
      <div className="loader"></div>
    </div>
  );
  
  if (error) return (
    <div className="error-container">
      <p>Error: {error}</p>
    </div>
  );

  const filteredProducts = filter === 'all' 
    ? products 
    : products.filter(product => product.style.toLowerCase() === filter);

  return (
    <div className="products-container">
      <div className="products-header">
        <h1>Our Collection</h1>
        <div className="filter-buttons">
          <button 
            className={filter === 'all' ? 'active' : ''} 
            onClick={() => setFilter('all')}
          >
            All
          </button>
          <button 
            className={filter === 'matte' ? 'active' : ''} 
            onClick={() => setFilter('matte')}
          >
            Matte
          </button>
          <button 
            className={filter === 'glossy' ? 'active' : ''} 
            onClick={() => setFilter('glossy')}
          >
            Glossy
          </button>
          <button 
            className={filter === 'holographic' ? 'active' : ''} 
            onClick={() => setFilter('holographic')}
          >
            Holographic
          </button>
        </div>
      </div>

      <div className="products-grid">
        {filteredProducts.map(product => (
          <div key={product.id} className="product-card">
            <div className="product-image">
              <img 
                src={`http://localhost/backend/${product.image_url}`} 
                alt={product.name} 
              />
            </div>
            <div className="product-details">
              <h3>{product.name}</h3>
              <p className="price">${product.price}</p>
              <p className="specs">
                {product.dimensions} â€¢ {product.style}
              </p>
              <Link to={`/product/${product.id}`} className="view-button">
                View Details
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Products;
