import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import './Navbar.css';

function Navbar() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [showDropdown, setShowDropdown] = useState(false);

  // This useEffect would check for user authentication status
  useEffect(() => {
    // Check authentication status from your auth service
    // Example: const authData = authService.getCurrentUser();
    // setIsLoggedIn(!!authData);
    // setUser(authData);
  }, []);

  const handleLogout = () => {
    // Implement logout logic
    setIsLoggedIn(false);
    setUser(null);
    setShowDropdown(false);
    // Example: authService.logout();
  };

  return (
    <nav className="navbar">
      <Link to="/" className="nav-brand">
        ThankYouStickers
      </Link>
      <div className="nav-center">
        <Link to="/products">Products</Link>
        <Link to="/about">About</Link>
        <Link to="/contact">Contact</Link>
      </div>
      <div className="nav-right">
        {!isLoggedIn ? (
          <>
            <Link to="/login" className="auth-link">Login</Link>
            <Link to="/signup" className="auth-link get-started">Get Started</Link>
          </>
        ) : (
          <div className="user-menu">
            <div 
              className="user-profile"
              onClick={() => setShowDropdown(!showDropdown)}
            >
              <AccountCircleIcon />
              <span>{user?.username}</span>
              <KeyboardArrowDownIcon />
            </div>
            
            {showDropdown && (
              <div className="dropdown-menu">
                {user?.role === 'admin' && (
                  <Link to="/admin">Admin Dashboard</Link>
                )}
                <Link to="/profile">Profile</Link>
                <Link to="/addresses">Addresses</Link>
                <Link to="/settings">Account Settings</Link>
                <button onClick={handleLogout}>Logout</button>
              </div>
            )}
          </div>
        )}
        <Link to="/cart" className="cart-icon">
          <ShoppingCartIcon />
        </Link>
      </div>
    </nav>
  );
}

export default Navbar;
