import { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import Modal from './Modal';
import NotificationOverlay from './NotificationOverlay';
import './AuthModal.css';
import './PasswordStrength.css';

const PasswordValidation = ({ password }) => {
  const requirements = [
    {
      text: "Minimum 8 characters",
      test: () => password.length >= 8
    },
    {
      text: "At least one uppercase letter",
      test: () => /[A-Z]/.test(password)
    },
    {
      text: "At least one number",
      test: () => /[0-9]/.test(password)
    },
    {
      text: "At least one special character (@#$%^&*)",
      test: () => /[@#$%^&*]/.test(password)
    }
  ];

  return (
    <div className="password-requirements">
      {requirements.map((req, index) => (
        <div key={index} className={`requirement ${req.test() ? 'valid' : 'invalid'}`}>
          <span className="requirement-icon">
            {req.test() ? 'âœ“' : 'âœ•'}
          </span>
          <span className="requirement-text">{req.text}</span>
        </div>
      ))}
    </div>
  );
};

const AuthModal = ({ isOpen, onClose }) => {
  const { setUser, checkAuth } = useAuth();
  const [isLogin, setIsLogin] = useState(true);
  const [isReset, setIsReset] = useState(false);
  const [registrationSuccess, setRegistrationSuccess] = useState(false);
  const [showNotification, setShowNotification] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    username: ''
  });
  const [error, setError] = useState('');
  const [showPasswordRequirements, setShowPasswordRequirements] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const validatePassword = (password) => {
    const requirements = [
      password.length >= 8,
      /[A-Z]/.test(password),
      /[0-9]/.test(password),
      /[@#$%^&*]/.test(password)
    ];
    return requirements.every(Boolean);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      if (!isLogin) {
        // Validate password requirements
        if (!validatePassword(formData.password)) {
          setError('Password must be at least 8 characters long and contain at least one uppercase letter, one number, and one special character (@#$%^&*)');
          setLoading(false);
          return;
        }

        if (formData.password !== formData.confirmPassword) {
          setError('Passwords do not match');
          setLoading(false);
          return;
        }
      }

      const response = await fetch(`http://localhost/backend/api/${isLogin ? 'login' : 'register'}.php`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: formData.username,
          email: formData.email,
          password: formData.password,
        }),
      });

      const data = await response.json();

      if (!data.success) {
        throw new Error(data.error || 'Something went wrong');
      }

      if (isLogin) {
        // Store user data and token
        localStorage.setItem('user', JSON.stringify(data.user));
        localStorage.setItem('token', data.token);
        onClose();
      } else {

        setShowNotification(true);
        setMessage('Registration successful! Please login to continue.');
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  if (registrationSuccess) {
    return (
      <Modal isOpen={isOpen} onClose={onClose}>
        <div className="auth-modal success-modal">
          <h2>Registration Successful! ðŸŽ‰</h2>
          <p>{message}</p>
          <button 
            className="submit-btn"
            onClick={() => {
              setRegistrationSuccess(false);
              setIsLogin(true);
            }}
          >
            Back to Login
          </button>
        </div>
      </Modal>
    );
  }

  return (
    <>
      <Modal isOpen={isOpen} onClose={onClose}>
        <div className="auth-modal">
          <h2>{isLogin ? 'Login' : 'Sign Up'}</h2>
          {error && <div className="error-message">{error}</div>}

          <form onSubmit={handleSubmit}>
            {!isLogin && (
              <div className="form-group">
                <label>Username</label>
                <input
                  type="text"
                  name="username"
                  value={formData.username}
                  onChange={handleChange}
                  required
                />
              </div>
            )}

            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                onFocus={() => !isLogin && setShowPasswordRequirements(true)}
                required
              />
              {!isLogin && showPasswordRequirements && (
                <PasswordValidation password={formData.password} />
              )}
            </div>

            {!isLogin && (
              <div className="form-group">
                <label>Confirm Password</label>
                <input
                  type="password"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  required
                />
              </div>
            )}

            <button type="submit" className="submit-btn" disabled={loading}>
              {isLogin ? 'Login' : 'Sign Up'}
            </button>
          </form>

          <div className="auth-links">
            <button 
              className="link-btn"
              onClick={() => setIsLogin(!isLogin)}
            >
              {isLogin ? "Don't have an account? Sign Up" : "Already have an account? Login"}
            </button>
          </div>
        </div>
      </Modal>

      {showNotification && (
        <NotificationOverlay
          message={message}
          onClose={() => {
            setShowNotification(false);
            onClose();
          }}
          showLoginButton={true}
        />
      )}
    </>
  );
};

export default AuthModal;
