import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Products.css';

function Products() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Here we'll fetch products from Firebase
    // For now, using dummy data
    setProducts([
      {
        id: 1,
        name: "Cute Cat Sticker",
        description: "Adorable cat sticker perfect for notebooks",
        dimensions: "3x3 inches",
        price: 2.99,
        image: "https://placeholder.com/300",
        style: "Matte"
      },
      // Add more dummy products...
    ]);
  }, []);

  return (
    <div className="products-container">
      <h1>Browse Our Stickers</h1>
      <div className="products-grid">
        {products.map(product => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p className="price">${product.price}</p>
            <p className="dimensions">Size: {product.dimensions}</p>
            <p className="style">Style: {product.style}</p>
            <Link to={`/product/${product.id}`} className="view-button">
              View Details
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Products;
