import { useState, useEffect } from 'react';
import './Admin.css';

function Admin() {
  const [productData, setProductData] = useState({
    name: '',
    description: '',
    dimensions: '',
    price: '',
    style: '',
    image: null,
  });
  const [message, setMessage] = useState('');
  const [banners, setBanners] = useState([]);
  const [bannerData, setBannerData] = useState({
    title: '',
    image: null,
  });
  const [activeTab, setActiveTab] = useState('products'); // 'products' or 'banners'

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();

      formData.append('name', productData.name.trim());
      formData.append('description', productData.description.trim());
      formData.append('dimensions', productData.dimensions.trim());
      formData.append('price', parseFloat(productData.price).toFixed(2));
      formData.append('style', productData.style);

      if (productData.image) {
        formData.append('image', productData.image);
      }

      console.log('Form data contents:');
      for (let pair of formData.entries()) {
        console.log(`${pair[0]}:`, pair[1]);
      }

      const response = await fetch('http://localhost/backend/api/add_product.php', {
        method: 'POST',
        body: formData,
      });

      const responseText = await response.text();
      console.log('Raw response:', responseText);

      let data;
      try {
        data = JSON.parse(responseText);
      } catch (parseError) {
        console.error('Failed to parse response:', responseText);
        throw new Error('Invalid server response: ' + responseText);
      }

      if (data.success) {
        setMessage('Product added successfully!');
        setProductData({
          name: '',
          description: '',
          dimensions: '',
          price: '',
          style: '',
          image: null,
        });
        const fileInput = document.querySelector('input[type="file"]');
        if (fileInput) {
          fileInput.value = '';
        }
      } else {
        throw new Error(data.error || 'Failed to add product');
      }
    } catch (error) {
      console.error('Error details:', error);
      setMessage('Error: ' + error.message);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProductData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file && file.size > 2 * 1024 * 1024) {
      alert('File size exceeds 2MB!');
      return;
    }
    setProductData((prev) => ({
      ...prev,
      image: file,
    }));
  };

  const fetchBanners = async () => {
    try {
      const response = await fetch('http://localhost/backend/api/get_banners.php');
      const data = await response.json();
      if (data.success) {
        setBanners(data.banners);
      }
    } catch (error) {
      console.error('Error fetching banners:', error);
    }
  };

  const handleBannerSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('title', bannerData.title);
      formData.append('image', bannerData.image);

      const response = await fetch('http://localhost/backend/api/add_banner.php', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (data.success) {
        setBannerData({ title: '', image: null });
        fetchBanners();
        setMessage('Banner added successfully!');
      } else {
        throw new Error(data.error);
      }
    } catch (error) {
      setMessage('Error: ' + error.message);
    }
  };

  const handleDeleteBanner = async (id) => {
    if (!window.confirm('Are you sure you want to delete this banner?')) return;

    try {
      const response = await fetch(`http://localhost/backend/api/delete_banner.php?id=${id}`, {
        method: 'DELETE',
      });

      const data = await response.json();
      if (data.success) {
        fetchBanners();
        setMessage('Banner deleted successfully!');
      } else {
        throw new Error(data.error);
      }
    } catch (error) {
      setMessage('Error: ' + error.message);
    }
  };

  useEffect(() => {
    if (activeTab === 'banners') {
      fetchBanners();
    }
  }, [activeTab]);

  return (
    <div className="admin-container">
      <h1>Admin Dashboard</h1>
      
      <div className="admin-tabs">
        <button 
          className={`tab-button ${activeTab === 'products' ? 'active' : ''}`}
          onClick={() => setActiveTab('products')}
        >
          Products
        </button>
        <button 
          className={`tab-button ${activeTab === 'banners' ? 'active' : ''}`}
          onClick={() => setActiveTab('banners')}
        >
          Banners
        </button>
      </div>

      {activeTab === 'products' ? (
        <form onSubmit={handleSubmit} className="admin-form">
          <div className="form-group">
            <label>Product Name</label>
            <input
              type="text"
              name="name"
              value={productData.name}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Description</label>
            <textarea
              name="description"
              value={productData.description}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Dimensions</label>
            <input
              type="text"
              name="dimensions"
              value={productData.dimensions}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Price</label>
            <input
              type="number"
              name="price"
              step="0.01"
              value={productData.price}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Style</label>
            <select name="style" value={productData.style} onChange={handleChange} required>
              <option value="">Select style</option>
              <option value="Matte">Matte</option>
              <option value="Glossy">Glossy</option>
              <option value="Holographic">Holographic</option>
            </select>
          </div>
          <div className="form-group">
            <label>Image</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              required
            />
          </div>
          <button type="submit" className="submit-button">
            Add Product
          </button>
        </form>
      ) : (
        <div className="banner-management">
          <form onSubmit={handleBannerSubmit} className="admin-form">
            <div className="form-group">
              <label>Banner Title (optional)</label>
              <input
                type="text"
                value={bannerData.title}
                onChange={(e) => setBannerData(prev => ({ ...prev, title: e.target.value }))}
              />
            </div>
            <div className="form-group">
              <label>Banner Image</label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => setBannerData(prev => ({ ...prev, image: e.target.files[0] }))}
                required
              />
            </div>
            <button type="submit" className="submit-button">
              Add Banner
            </button>
          </form>

          <div className="banner-list">
            <h2>Current Banners</h2>
            {banners.map(banner => (
              <div key={banner.id} className="banner-item">
                <img 
                  src={`http://localhost/backend/${banner.image_url}`} 
                  alt={banner.title} 
                />
                <div className="banner-info">
                  <p>{banner.title}</p>
                  <button 
                    onClick={() => handleDeleteBanner(banner.id)}
                    className="delete-button"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
      
      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default Admin;
