import { useState, useEffect } from 'react';
import './ProductDetail.css';

function ProductDetail() {
  const [product, setProduct] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    fetchProductDetails();
  }, []);

  const fetchProductDetails = async () => {
    try {
      const response = await fetch(`http://localhost/backend/api/get_product.php?id=${id}`);
      const data = await response.json();
      
      if (data.success) {
        setProduct(data.product);
      } else {
        setError(data.error);
      }
    } catch (error) {
      setError('Failed to fetch product details');
    } finally {
      setLoading(false);
    }
  };

  const handleQuantityChange = (change) => {
    setQuantity(prev => Math.max(1, prev + change));
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">Error: {error}</div>;
  if (!product) return <div className="error">Product not found</div>;

  return (
    <div className="product-detail-container">
      <div className="product-detail-grid">
        {/* Left side - Image */}
        <div className="product-image-container">
          <img 
            src={`http://localhost/backend/${product.image_url}`} 
            alt={product.name} 
            className="product-main-image"
          />
        </div>

        {/* Right side - Product Info */}
        <div className="product-info-container">
          <h1 className="product-title">{product.name}</h1>
          
          <div className="product-price">
            ${parseFloat(product.price).toFixed(2)} USD
          </div>

          <div className="product-specs">
            <div className="spec-item">
              <span className="spec-label">Dimensions:</span>
              <span className="spec-value">{product.dimensions}</span>
            </div>
            <div className="spec-item">
              <span className="spec-label">Style:</span>
              <span className="spec-value">{product.style}</span>
            </div>
          </div>

          <div className="quantity-selector">
            <button 
              className="quantity-btn"
              onClick={() => handleQuantityChange(-1)}
              disabled={quantity <= 1}
            >
              −
            </button>
            <input 
              type="number" 
              value={quantity} 
              onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value) || 1))}
              className="quantity-input"
            />
            <button 
              className="quantity-btn"
              onClick={() => handleQuantityChange(1)}
            >
              +
            </button>
          </div>

          <button className="add-to-cart-btn">
            Add to Cart
          </button>

          <div className="product-description">
            <h2>Description</h2>
            <p>{product.description}</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
