import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './ProductDetail.css';

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    const fetchProductDetails = async () => {
      try {
        const response = await fetch(`http://localhost/backend/api/get_product.php?id=${id}`);
        const data = await response.json();

        if (data.success) {
          setProduct(data.product);
          setTotalPrice(data.product.price); // Initialize with single unit price
        } else {
          setError(data.error);
        }
      } catch (error) {
        setError('Failed to fetch product details');
      } finally {
        setLoading(false);
      }
    };

    fetchProductDetails();
  }, [id]);

  useEffect(() => {
    if (product) {
      setTotalPrice((product.price * quantity).toFixed(2));
    }
  }, [quantity, product]);

  const handleQuantityChange = (value) => {
    const newQuantity = quantity + value;
    if (newQuantity >= 1) {
      setQuantity(newQuantity);
    }
  };

  const handleAddToCart = () => {
    const cartItem = {
      ...product,
      quantity,
      totalPrice
    };
    console.log('Adding to cart:', cartItem);
    // Add cart functionality here
  };

  if (loading) return (
    <div className="loading-container">
      <div className="loader"></div>
    </div>
  );

  if (error) return (
    <div className="error-container">
      <p>Error: {error}</p>
    </div>
  );

  if (!product) return null;

  return (
    <div className="product-detail-container">
      <div className="product-detail">
        <div className="product-image-section">
          <img 
            src={`http://localhost/backend/${product.image_url}`} 
            alt={product.name} 
          />
        </div>
        <div className="product-info-section">
          <h1>{product.name}</h1>
          <div className="price-section">
            <span className="current-price">NPR {totalPrice}</span>
            <span className="original-price">NPR {(totalPrice * 1.25).toFixed(2)}</span>
            <span className="discount">5% off</span>
          </div>
          <div className="description-section">
            <h3>Description</h3>
            <p>{product.description}</p>
          </div>
          <div className="specs-section">
            <div className="spec-item">
              <span className="spec-label">Dimensions:</span>
              <span className="spec-value">{product.dimensions}</span>
            </div>
            <div className="spec-item">
              <span className="spec-label">Style:</span>
              <span className="spec-value">{product.style}</span>
            </div>
          </div>
          <div className="purchase-section">
            <div className="quantity-control">
              <button 
                className="quantity-btn" 
                onClick={() => handleQuantityChange(-1)}
                disabled={quantity <= 1}
              >
                −
              </button>
              <span className="quantity-display">{quantity}</span>
              <button 
                className="quantity-btn" 
                onClick={() => handleQuantityChange(1)}
              >
                +
              </button>
            </div>
            <button 
              className="add-to-cart-btn"
              onClick={handleAddToCart}
            >
              Add to Cart • NPR {totalPrice}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
