import { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import Modal from './Modal';
import './AuthModal.css';

const AuthModal = ({ isOpen, onClose }) => {
  const { setUser } = useAuth();
  const [isLogin, setIsLogin] = useState(true);
  const [isReset, setIsReset] = useState(false);
  const [registrationSuccess, setRegistrationSuccess] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    username: ''
  });
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!isLogin && formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    try {
      const endpoint = isReset ? 'reset_password' : (isLogin ? 'login' : 'register');
      const response = await fetch(`http://localhost/backend/api/${endpoint}.php`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify(formData)
      });

      const data = await response.json();

      if (data.success) {
        if (isLogin) {
          setUser(data.user);
          onClose();
        } else if (!isReset) {
          setRegistrationSuccess(true);
          // Clear form data
          setFormData({
            email: '',
            password: '',
            confirmPassword: '',
            username: ''
          });
        } else {
          setError('Password reset link sent to your email');
          setTimeout(() => {
            setIsReset(false);
          }, 3000);
        }
      } else {
        setError(data.error || 'Authentication failed');
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  if (registrationSuccess) {
    return (
      <Modal isOpen={isOpen} onClose={onClose}>
        <div className="auth-modal success-modal">
          <h2>Registration Successful! ðŸŽ‰</h2>
          <p>Your account has been created successfully.</p>
          <button 
            className="submit-btn"
            onClick={() => {
              setRegistrationSuccess(false);
              setIsLogin(true);
            }}
          >
            Back to Login
          </button>
        </div>
      </Modal>
    );
  }

  return (
    <Modal isOpen={isOpen} onClose={onClose}>
      <div className="auth-modal">
        <h2>{isReset ? 'Reset Password' : (isLogin ? 'Login' : 'Sign Up')}</h2>
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <div className="form-group">
              <label>Username</label>
              <input
                type="text"
                name="username"
                value={formData.username}
                onChange={handleChange}
                required
              />
            </div>
          )}

          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          {!isReset && (
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>
          )}

          {!isLogin && !isReset && (
            <div className="form-group">
              <label>Confirm Password</label>
              <input
                type="password"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleChange}
                required
              />
            </div>
          )}

          <button type="submit" className="submit-btn">
            {isReset ? 'Send Reset Link' : (isLogin ? 'Login' : 'Sign Up')}
          </button>
        </form>

        <div className="auth-links">
          {!isReset && (
            <button 
              className="link-btn"
              onClick={() => setIsLogin(!isLogin)}
            >
              {isLogin ? "Don't have an account? Sign Up" : "Already have an account? Login"}
            </button>
          )}
          
          {isLogin && (
            <button 
              className="link-btn"
              onClick={() => setIsReset(!isReset)}
            >
              {isReset ? "Back to Login" : "Forgot Password?"}
            </button>
          )}
        </div>
      </div>
    </Modal>
  );
};

export default AuthModal;
