import { useState, useEffect } from 'react';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import './Admin.css';

function Admin() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingProduct, setEditingProduct] = useState(null);
  const [productData, setProductData] = useState({
    name: '',
    description: '',
    dimensions: '',
    price: '',
    style: '',
    image: null,
  });
  const [message, setMessage] = useState('');
  const [banners, setBanners] = useState([]);
  const [bannerData, setBannerData] = useState({
    title: '',
    image: null,
  });
  const [activeTab, setActiveTab] = useState('products');

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost/backend/api/get_products.php');
      const data = await response.json();
      if (data.success) {
        setProducts(data.products);
      }
    } catch (error) {
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setProductData({
      name: product.name,
      description: product.description,
      dimensions: product.dimensions,
      price: product.price,
      style: product.style,
      image: null,
    });
  };

  const handleDelete = async (productId) => {
    if (!window.confirm('Are you sure you want to delete this product?')) return;

    try {
      const response = await fetch('http://localhost/backend/api/delete_product.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ id: productId }),
      });

      const data = await response.json();
      if (data.success) {
        setMessage('Product deleted successfully');
        fetchProducts();
      } else {
        setMessage('Error: ' + data.error);
      }
    } catch (error) {
      setMessage('Error deleting product');
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    
    try {
      const formData = new FormData();
      formData.append('id', editingProduct.id);
      Object.keys(productData).forEach(key => {
        if (key === 'price') {
          formData.append(key, parseFloat(productData[key]).toFixed(2));
        } else {
          formData.append(key, productData[key]);
        }
      });

      const response = await fetch('http://localhost/backend/api/update_product.php', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (data.success) {
        setMessage('Product updated successfully');
        setEditingProduct(null);
        fetchProducts();
      } else {
        setMessage('Error: ' + data.error);
      }
    } catch (error) {
      setMessage('Error updating product');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      Object.keys(productData).forEach(key => {
        if (key === 'price') {
          formData.append(key, parseFloat(productData[key]).toFixed(2));
        } else {
          formData.append(key, productData[key]);
        }
      });

      const response = await fetch('http://localhost/backend/api/add_product.php', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      
      if (data.success) {
        setMessage('Product added successfully!');
        setProductData({
          name: '',
          description: '',
          dimensions: '',
          price: '',
          style: '',
          image: null,
        });
        document.querySelector('input[type="file"]').value = '';
      } else {
        throw new Error(data.error || 'Failed to add product');
      }
    } catch (error) {
      setMessage('Error: ' + error.message);
    }
  };

  const handleBannerSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('title', bannerData.title);
      formData.append('image', bannerData.image);

      const response = await fetch('http://localhost/backend/api/add_banner.php', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (data.success) {
        setBannerData({ title: '', image: null });
        fetchBanners();
        setMessage('Banner added successfully!');
      } else {
        throw new Error(data.error);
      }
    } catch (error) {
      setMessage('Error: ' + error.message);
    }
  };

  const fetchBanners = async () => {
    try {
      const response = await fetch('http://localhost/backend/api/get_banners.php');
      const data = await response.json();
      if (data.success) {
        setBanners(data.banners);
      }
    } catch (error) {
      console.error('Error fetching banners:', error);
    }
  };

  const handleDeleteBanner = async (id) => {
    if (!window.confirm('Are you sure you want to delete this banner?')) return;

    try {
      const response = await fetch('http://localhost/backend/api/delete_banner.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ id: id })
      });

      const data = await response.json();
      if (data.success) {
        fetchBanners();
        setMessage('Banner deleted successfully!');
      } else {
        throw new Error(data.error || 'Failed to delete banner');
      }
    } catch (error) {
      console.error('Delete error:', error);
      setMessage('Error: ' + error.message);
    }
  };

  const handleProductChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'image') {
      setProductData(prev => ({
        ...prev,
        image: files[0]
      }));
    } else {
      setProductData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleBannerImageChange = (e) => {
    const file = e.target.files[0];
    setBannerData(prev => ({
      ...prev,
      image: file
    }));
  };

  return (
    <div className="admin-dashboard">
      <div className="dashboard-header">
        <h1>Orders</h1>
        <div className="header-actions">
          <button className="import-btn">↓ Import</button>
          <button className="export-btn">↑ Export</button>
        </div>
      </div>

      <div className="products-table">
        <div className="table-header">
          <div className="checkbox-cell">
            <input type="checkbox" />
          </div>
          <div className="header-cell">Product</div>
          <div className="header-cell">Type</div>
          <div className="header-cell">Status</div>
          <div className="header-cell">Price</div>
          <div className="header-cell">Date</div>
          <div className="header-cell"></div>
        </div>

        {products.map(product => (
          <div key={product.id} className="table-row">
            <div className="checkbox-cell">
              <input type="checkbox" />
            </div>
            <div className="product-cell">
              <img 
                src={`http://localhost/backend/${product.image_url}`} 
                alt={product.name}
                className="product-thumbnail" 
              />
              <span>{product.name}</span>
            </div>
            <div className="cell">{product.style}</div>
            <div className="cell">
              <span className="status-badge active">Active</span>
            </div>
            <div className="cell">NPR {product.price}</div>
            <div className="cell">{new Date(product.created_at).toLocaleDateString()}</div>
            <div className="actions-cell">
              <button onClick={() => handleEdit(product)} className="action-btn edit">
                <EditIcon />
              </button>
              <button onClick={() => handleDelete(product.id)} className="action-btn delete">
                <DeleteIcon />
              </button>
              <button className="action-btn more">
                <MoreVertIcon />
              </button>
            </div>
          </div>
        ))}
      </div>

      {editingProduct && (
        <div className="edit-modal">
          <div className="modal-content">
            <h2>Edit Product</h2>
            <form onSubmit={handleUpdate}>
              <div className="form-group">
                <label>Product Name</label>
                <input
                  type="text"
                  value={productData.name}
                  onChange={(e) => setProductData({...productData, name: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Description</label>
                <textarea
                  value={productData.description}
                  onChange={(e) => setProductData({...productData, description: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Price</label>
                <input
                  type="number"
                  step="0.01"
                  value={productData.price}
                  onChange={(e) => setProductData({...productData, price: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Style</label>
                <select 
                  value={productData.style}
                  onChange={(e) => setProductData({...productData, style: e.target.value})}
                  required
                >
                  <option value="">Select style</option>
                  <option value="Matte">Matte</option>
                  <option value="Glossy">Glossy</option>
                  <option value="Holographic">Holographic</option>
                </select>
              </div>
              <div className="form-group">
                <label>New Image (optional)</label>
                <input
                  type="file"
                  onChange={(e) => setProductData({...productData, image: e.target.files[0]})}
                />
              </div>
              <div className="modal-actions">
                <button type="button" onClick={() => setEditingProduct(null)}>Cancel</button>
                <button type="submit">Update Product</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {message && <div className="message">{message}</div>}
    </div>
  );
}

export default Admin;
