import { createContext, useState, useContext, useEffect } from 'react';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  const updateUser = (userData) => {
    console.log('Updating user with:', userData);
    setUser(userData);
    // Save to localStorage as a backup
    if (userData) {
      localStorage.setItem('user', JSON.stringify(userData));
    } else {
      localStorage.removeItem('user');
    }
  };

  const checkAuth = async () => {
    try {
      const response = await fetch('http://localhost/backend/api/check_auth.php', {
        method: 'GET',
        credentials: 'include',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });

      const data = await response.json();
      console.log('Check auth response:', data);
      
      if (data.success && data.user) {
        updateUser(data.user);
      } else {
        updateUser(null);
      }
    } catch (error) {
      console.error('Auth check failed:', error);
      updateUser(null);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Try to get user from localStorage on initial load
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      try {
        updateUser(JSON.parse(savedUser));
      } catch (e) {
        console.error('Error parsing saved user:', e);
      }
    }
    checkAuth();
  }, []);

  const logout = async () => {
    try {
      const response = await fetch('http://localhost/backend/api/logout.php', {
        method: 'POST',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        updateUser(null);
      }
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  return (
    <AuthContext.Provider value={{ user, setUser: updateUser, loading, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
