import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Products.css';

function Products() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost/thankyoustickers/backend/api/get_products.php');
      const data = await response.json();
      
      if (data.success) {
        setProducts(data.products);
      } else {
        setError(data.error);
      }
    } catch (error) {
      setError('Failed to fetch products');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="products-container">
      <h1>Browse Our Stickers</h1>
      <div className="products-grid">
        {products.map(product => (
          <div key={product.id} className="product-card">
            <img 
              src={`http://localhost/thankyoustickers/backend/${product.image_url}`} 
              alt={product.name} 
            />
            <h3>{product.name}</h3>
            <p className="price">${product.price}</p>
            <p className="dimensions">Size: {product.dimensions}</p>
            <p className="style">Style: {product.style}</p>
            <Link to={`/product/${product.id}`} className="view-button">
              View Details
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Products;
